 $(window).on('load', function () {
 var $preloader = $('#page-preloader'),
 $spinner = $preloader.find('.spinner');
 $spinner.fadeOut();
 $preloader.delay(350).fadeOut('slow');
 });


 	$('.js-offcanvas-has-events').click(function(){
		if($(this).hasClass('is-open')){
	 		$(this).removeClass('is-open');
	 		$('.offcanvas-transform.js-offcanvas-done').removeClass('in');	
 		}else{
	 		$(this).addClass('is-open');
	 		$('.offcanvas-transform.js-offcanvas-done').addClass('in');
	 	}
 	});

 	$('.dropdown.none').click(function(){
 		if($('.dropdown-menu').hasClass("shown")){
 			$(this).removeClass("active");
 			$('.dropdown-menu').removeClass("shown");
 		}else{
 			$(this).addClass("active");
 			$('.dropdown-menu').addClass("shown");
 		}
 	});

